mapper.sql:
WITH base AS ( SELECT d.STN_NM, d.LAT, d.LON, d.AVG, d.TM_DATE, d.AIR_TYPE, ROW_NUMBER() OVER (PARTITION BY d.STN_NM ORDER BY d.TM_DATE DESC) AS rn FROM DUST d WHERE d.AIR_TYPE = #{airType}
AND TRUNC(d.TM_DATE) = TO_DATE(#{day}, 'YYYY-MM-DD')
AND d.LAT BETWEEN #{minLat} AND #{maxLat} AND d.LON BETWEEN #{minLon} AND #{maxLon}
) SELECT b.STN_NM, b.LAT, b.LON, b.AVG, b.TM_DATE, b.AIR_TYPE FROM base b WHERE b.rn = 1
