mapper.select:
SELECT d.DUST_NO AS dustNo, TO_CHAR(d.TM_DATE, 'YYYY-MM-DD HH24:MI:SS') AS tm, d.STN_NM AS stnNm, d.LAT AS lat, d.LON AS lon, TRIM(d.ORG) AS org, d.PM10 AS pm10, d.AVG AS avg, d.MAX AS max, d.CNT AS cnt FROM ( SELECT d.*, ROW_NUMBER() OVER (PARTITION BY d.STN_NM ORDER BY d.TM_DATE DESC) rn FROM DUST d WHERE d.LAT BETWEEN #{minLat} AND #{maxLat} AND d.LON BETWEEN #{minLon} AND #{maxLon}
AND REPLACE(TRIM(d.ORG),' ','') = REPLACE(TRIM(#{airType}),' ','')
AND TRUNC(d.TM_DATE) = TO_DATE(#{day}, 'YYYY-MM-DD')
) d WHERE d.rn = 1
AND ROWNUM <= #{limit}

SELECT d.DUST_NO AS dustNo, TO_CHAR(d.TM_DATE, 'YYYY-MM-DD HH24:MI:SS') AS tm, d.STN_NM AS stnNm, d.LAT AS lat, d.LON AS lon, TRIM(d.ORG) AS org, d.PM10 AS pm10, d.AVG AS avg, d.MAX AS max, d.CNT AS cnt FROM ( SELECT d.*, ROW_NUMBER() OVER (PARTITION BY d.STN_NM ORDER BY d.TM_DATE DESC) rn FROM DUST d
REPLACE(TRIM(d.ORG),' ','') = REPLACE(TRIM(#{airType}),' ','')
TRUNC(d.TM_DATE) = TO_DATE(#{day}, 'YYYY-MM-DD')
) d WHERE d.rn = 1
AND ROWNUM <= #{limit}
