mapper.select:
SELECT A.*, B.TOTAL_CNT FROM ( SELECT TT3.RNUM AS NO, TT3.LOG_NO, TT3.USER_NO, TT3.SESSION_ID, TT3.QUESTION, TT3.ANSWER, TO_CHAR(TT3.REG_DT,'YYYY-MM-DD HH24:MI:SS') AS REG_DT FROM ( SELECT ROWNUM AS RNUM, TT2.* FROM ( SELECT T1.* FROM JM.CHAT_LOG T1
ORDER BY T1.REG_DT DESC ) TT2
WHERE ROWNUM <= ( NVL(#{pageSize},10) * (NVL(#{pageNo},1) - 1) + NVL(#{pageSize},10) ) ) TT3
WHERE RNUM >= ( NVL(#{pageSize},10) * (NVL(#{pageNo},1) - 1) + 1 )
) A CROSS JOIN ( SELECT COUNT(*) AS TOTAL_CNT FROM JM.CHAT_LOG
) B

mapper.update:
UPDATE JM.CHAT_LOG SET
USER_NO = #{userNo}, SESSION_ID = #{sessionId}, QUESTION = #{question}, ANSWER = #{answer},
WHERE LOG_NO = #{logNo}

mapper.select:
SELECT *
  FROM (
    SELECT t.LOG_NO, t.USER_NO, t.SESSION_ID, t.QUESTION, t.ANSWER,
           TO_CHAR(t.REG_DT,'YYYY-MM-DD HH24:MI:SS') AS REG_DT
      FROM JM.CHAT_LOG t
     WHERE t.SESSION_ID = #{sessionId}
       AND t.USER_NO = #{userNo}
     ORDER BY t.REG_DT DESC
  )
 WHERE ROWNUM <= #{limit}
